import React, { Component, } from 'react';
import { Table, Button,Tabs, } from 'antd';
import './App.css';
import "antd/dist/antd.css";
import Item from 'antd/lib/list/Item';


const columns = [{
  title: 'Name',
  dataIndex: 'name',
}, {
  title: 'Age',
  dataIndex: 'age',
}, {
  title: 'Address',
  dataIndex: 'address',
}];

const data = [];
for (let i = 0; i < 46; i++) {
  data.push({
    key: i,
    name: `Edward King ${i}`,
    age: 32,
    address: `London, Park Lane no. ${i}`,
  });
}

const TabPane = Tabs.TabPane;
function callback(key) {
  console.log(key);
}


class App extends React.Component {
  state = {
    selectedRowKeys: [], 
    loading: false,
  };

  start = () => {
    this.setState({ loading: true });
    setTimeout(() => {
      this.setState({
        selectedRowKeys: [],
        loading: false,
      });
    }, 1000);
  }


  onSelectChange = (selectedRowKeys) => {
    console.log('selectedRowKeys changed: ', selectedRowKeys);
    this.setState({ selectedRowKeys });
  }

  render() {

      var options = [];
      options.push(<option key={"Introduction"}>Introduction</option>);
      for (var item in menuList) {
        options.push(
          <option key={menuList[item].key}>{menuList[item].title}</option>
        );
      }
      return (
        <React.Fragment>
          <select value={this.state.selectValue} onChange={this.selectedOptions}>
            {options}
          </select>
          <Tabs
            selectedIndex={this.state.tabIndex}
            onSelect={tabIndex => this.setState({ tabIndex })}
          >
            <TabList>
              {this.state.allTabs.map((item, index) => (
                <React.Fragment>
                  <Tab key={item.title}>
                    {item.title}
                    <span onClick={() => this.closeButton(index)}>
                      <img
                        src={close}
                        alt="logo"
                        style={{ height: "20px", width: "20px" }}
                      />
                    </span>
                  </Tab>
                </React.Fragment>
              ))}
            </TabList>
            {this.state.allTabs.map(item => {
              return (
                <TabPanel key={item.title}>
                  {item.type === "grid" ? (
                    <Grid
                      columns={columns[item.title.toLocaleLowerCase()]}
                      childItem={item.child}
                    />
                  ) : (
                    <div>Need to Implement</div>
                  )}
                </TabPanel>
              );
            })}
          </Tabs>
          {/* No Need of Button for now 
          <Button onClick={this.addmore}>add more</Button>
          */}
        </React.Fragment>


    );
  }
}



  export default App;
