import React, { Component, } from 'react';
import { Table, Button, Tabs, Form, Input } from 'antd';
import "antd/dist/antd.css";
import Item from 'antd/lib/list/Item';


const columns = [{
  title: 'Name',
  dataIndex: 'name',
}, {
  title: 'Age',
  dataIndex: 'age',
}, {
  title: 'Address',
  dataIndex: 'address',
}];

const data = [];
for (let i = 0; i < 46; i++) {
  data.push({
    key: i,
    name: `Edward King ${i}`,
    age: 32,
    address: `London, Park Lane no. ${i}`,
  });
}

class App extends React.Component {

  state = {
    selectedRowKeys: [],
    loading: false,
  };
  selectedOptions = event => {
    var value = event.target.value;
    var checkTabs = this.state.allTabs.find(item => {
      if (item.title === value) {
        this.setState({ tabIndex: item.index });
        return true;
      }
      return item.title === value;
    });
    if (!checkTabs) {
      var allTabs = [...this.state.allTabs];
      menuList.find(item => {
        if (item.title === value) {
          allTabs.push(item);
          this.setState({ allTabs: allTabs, tabIndex: allTabs.length - 1 });
        }
      });
    }
    this.setState({ selectValue: value });
  };

  start = () => {
    this.setState({ loading: true });
    setTimeout(() => {
      this.setState({
        selectedRowKeys: [],
        loading: false,
      });
    }, 1000);
  }
  onSelectChange = (selectedRowKeys) => {
    console.log('selectedRowKeys changed: ', selectedRowKeys);
    this.setState({ selectedRowKeys });
  }

  render() {
    const { loading, selectedRowKeys } = this.state;
    const rowSelection = {
      selectedRowKeys,
      onChange: this.onSelectChange,
    };


    const hasSelected = selectedRowKeys.length > 0;
    return (
      <div>
        <div style={{ marginBottom: 16 }}>
          <Button
            type="primary"
            onClick={this.start}
            disabled={!hasSelected}
            loading={loading}
          >
            Reload
          </Button>
          <span style={{ marginLeft: 8, }}>
            {hasSelected ? `Selected ${selectedRowKeys.length} items` : ''}
          </span>
        </div>
        <Table rowSelection={rowSelection} columns={columns} dataSource={data} />

        <Form className="login" key={columns.key}>
          {columns.map(item => (
            <Form.Item key={item.title} label={item.title}>
              <Input value={columns[item.title]} style={{ width: "200px" }} />
            </Form.Item>
          ))}
        </Form>
      </div>
    );
  }
}

export default App;
